package mira.lobby.API;import org.bukkit.Bukkit;import org.bukkit.ChatColor;import org.bukkit.entity.Player;import org.bukkit.scoreboard.*;public class ScoreBord {    public static void ScoreBordAdd(Player p ){        ScoreboardManager m = Bukkit.getScoreboardManager();        Scoreboard b = m.getNewScoreboard();        Objective o = b.registerNewObjective("MRNSBSystem", "" );        o.setDisplaySlot(DisplaySlot.SIDEBAR);        o.setDisplayName(ChatColor.AQUA + "§l>>MiraNetWork MRN<<");        Score s1 = o.getScore(ChatColor.GOLD + "§l"+  p.getName() + "様");        s1.setScore(15);        Score s2 = o.getScore(ChatColor.AQUA + "§lMRNにようこそ！");        s2.setScore(14);        Score s3 = o.getScore( "       ");        s3.setScore(13);        Score s4 = o.getScore("      ");        s4.setScore(12);        Score s5 = o.getScore("これいる？");        s5.setScore(11);        Score s6 = o.getScore(" ");        s6.setScore(10);        Score s7 = o.getScore("  ");        s7.setScore(9);        Score s8 = o.getScore("   ");        s8.setScore(8);        Score s9 = o.getScore(ChatColor.GREEN + "オンライン人数");        s9.setScore(7);        Score s10 = o.getScore( ChatColor.GREEN +""+ Bukkit.getOnlinePlayers().size() + ChatColor.BLACK +"/" +ChatColor.DARK_GREEN + Bukkit.getMaxPlayers());        s10.setScore(6);        Score s11 = o.getScore(" 動きません ");        s11.setScore(5);        Score s12 = o.getScore(" 誰か動かしかた教えて");        s12.setScore(4);        Score s13 = o.getScore( " ");        s13.setScore(3);        Score s14 = o.getScore(" ");        s14.setScore(2);        Score s15 = o.getScore( ChatColor.WHITE + "§l>>MRN MiraNetWork<<");        s15.setScore(1);        p.setScoreboard(b);    }}