package mira.lobby.Commands;import org.bukkit.Bukkit;import org.bukkit.ChatColor;import org.bukkit.Location;import org.bukkit.command.Command;import org.bukkit.command.CommandExecutor;import org.bukkit.command.CommandSender;import org.bukkit.entity.Player;public class LobbyCommnad implements CommandExecutor{    @Override    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {        if (command.getName().equalsIgnoreCase("lobby")){            Player p = (Player) sender;            p.teleport(new Location(Bukkit.getWorld("Lobbytest"), -2.5, 77, -23));            sender.sendMessage( ChatColor.BOLD + "ロビーにテレポートしました");            return true;        }        return false;    }}