package mira.lobby.Commands;import org.bukkit.Bukkit;import org.bukkit.ChatColor;import org.bukkit.Sound;import org.bukkit.command.Command;import org.bukkit.command.CommandExecutor;import org.bukkit.command.CommandSender;import org.bukkit.entity.Player;public class VandalismKickCommand implements CommandExecutor {    @Override    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {        if (command.getName().equalsIgnoreCase("allkick")) {            if (sender.isOp()){            for (Player on : Bukkit.getOnlinePlayers()) {                if (!on.isOp()) {                    on.kickPlayer("Adminもしくはオーナによりサーバに居た人(OPは含まない)が強制キックされました。");                    Bukkit.getServer().broadcastMessage("OP以外が強制Kickされました");                }            }            }else {                Player p = (Player) sender;                sender.sendMessage(ChatColor.RED + "§lError> 権限がありません。");                if(sender instanceof Player) {                    p.playSound(p.getLocation(), Sound.BLAZE_DEATH, 1, 1);                }            }        }        return false;    }}